sp_widget.script:
// form functionality - URL parameter driven
(function($sp, input, data, options, gs) {
	/* "use strict"; -linter issues */



	///////////////////////////////////
	// Pre-populate the 'data' variable
	///////////////////////////////////
	data.f = {};
	data.recordAddedMsg = gs.getMessage("Record Added");
	data.updatedMsg = gs.getMessage("updated_uppercase");
	data.process_msg = "Data obj2 Initialized";


	data.table = data.table || $sp.getParameter("t") || $sp.getParameter("table") || $sp.getParameter("sl_table") || options.table;
	data.sys_id = data.sys_id || $sp.getParameter("sys_id") || $sp.getParameter("sl_sys_id") || options.sys_id;
	if (!data.sys_id && options.sys_id_required != "true")
		data.sys_id = "-1";
	data.view = options.view || $sp.getParameter("view") || $sp.getParameter("v"); // no default






	///////////////////////////////////
	// Generate values for the Preview (ToDo!)
	///////////////////////////////////

	data.preview_record = "1c741bd70b2322007518478d83673af3";
//	data.preview_record = new GlideRecord(...) // orderByDesc updated

	data.preview_event = "";
//	data.preview_event = new GlideRecord(...) // orderByDesc updated



	var sys_id_str = (data.sys_id || "").toString();
	if (sys_id_str.match(/^\w{32}$/) != null) {
		data.isTargetValid = true;
		data.preview_url =
			"notification_preview_html_body.do?sysparm_record_id="
			+ data.preview_record
			+ "&sysparm_user_id=" + "6816f79cc0a8016401c5a33be04be441"
			+ "&sysparm_event_id=" + data.preview_event
			+ "&sysparm_notification_id=" + data.sys_id
			+ "&sysparm_changed_fields="
			+ "&sys_target=sysevent_email_action"
			+ "&sys_uniqueValue=" + data.sys_id;


	}
})($sp, input, data, options, gs);
